void printLine(int letter, int line) {
    for (int i = 0; i < 7; ++i) {
        cout << alphabet[letter][line][i];
    }
}

int main() {
    string s;
    cin >> s;
    for (int i = 0; s[i] != '\0'; i++) {
        if (s[i] >= 'a' && s[i] <= 'z') {
            s[i] = s[i] - 32;
        }
    }
    cout << endl;

    for (int j = 0; j < 7; ++j) {//because we have 7 lines for each letter
        for (int i = 0; i < s.length(); ++i) {
            printLine(s[i] - 65, j);
            cout << " ";
        }
        cout << endl;
    }

    return 0;
}